css:
body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    color: #333;
}

h2, h3, h4 {
    color: #34495e;
    margin-top: 20px;
}

h2 {
    border-bottom: 2px solid #ecf0f1;
    padding-bottom: 10px;
}

.text-center {
    text-align: center;
}

.user-story, .feature {
    background-color: #f9f9f9;
    border: 1px solid #e0e0e0;
    border-radius: 5px;
    padding: 15px;
    margin-bottom: 20px;
}

table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 20px;
}

th, td {
    border: 1px solid #ecf0f1;
    padding: 8px;
    text-align: left;
}

th {
    background-color: #f2f2f2;
    font-weight: bold;
}

@media print {
    .user-story, .feature {
        page-break-inside: avoid;
    }
}
