css:
body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    color: #333;
}

h2, h3, h4 {
    color: #34495e;
    margin-top: 20px;
}

h2 {
    border-bottom: 2px solid #ecf0f1;
    padding-bottom: 10px;
}

.text-center {
    text-align: center;
}

.user-story, .feature {
    background-color: #f9f9f9;
    border: 1px solid #e0e0e0;
    border-radius: 5px;
    padding: 15px;
    margin-bottom: 20px;
}

table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 20px;
}

th, td {
    border: 1px solid #ecf0f1;
    padding: 8px;
    text-align: left;
}

th {
    background-color: #f2f2f2;
    font-weight: bold;
}

@media print {
    .user-story, .feature {
        page-break-inside: avoid;
    }
}

@page {
    size: landscape;
}
body {
    font-family: Arial, sans-serif;
    background-color: #f0f0f0;
    margin: 0;
    padding: 20px;
}

h1 {
    text-align: center;
    font-size: 24px;
    margin-bottom: 20px;
}

.header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 20px;
}

.logo {
    max-height: 60px;
    max-width: 120px;
}

.document-info {
    text-align: right;
}

.document-info h2 {
    margin: 0;
    font-size: 20px;
}

.document-info p {
    margin: 5px 0 0;
    font-size: 14px;
    color: #666;
}

.lean-canvas {
    display: grid;
    grid-template-columns: repeat(10, 1fr);
    gap: 10px;
    max-width: 1200px;
    margin: auto;
}

.box {
    border: 1px solid #ccc;
    padding: 15px;
    background-color: #fff;
    border-radius: 5px;
    position: relative;
}

.box.problems {
    grid-column: 1 / span 2;
    grid-row: 1 / span 2;
    background-color: #e57373;
}

.box.customer-segments {
    grid-column: 9 / span 2;
    grid-row: 1 / span 2;
    background-color: #ba68c8;
}

.box.unique-value-proposition {
    grid-column: 5 / span 2;
    grid-row: 1 / span 1;
    background-color: #4db6ac;
}

.box.solutions {
    grid-column: 3 / span 2;
    grid-row: 1 / span 1;
    background-color: #64b5f6;
}

.box.channels {
    grid-column: 7 / span 2;
    grid-row: 2 / span 1;
    background-color: #ffb74d;
}

.box.cost-structure {
    grid-column: 1 / span 5;
    grid-row: 3 / span 1;
    background-color: #ff8a65;
}

.box.revenue-streams {
    grid-column: 6 / span 5;
    grid-row: 3 / span 1;
    background-color: #ffd54f;
}

.box.key-metrics {
    grid-column: 3 / span 2;
    grid-row: 2 / span 1;
    background-color: #9575cd;
}

.box.unfair-advantage {
    grid-column: 7 / span 2;
    grid-row: 1 / span 1;
    background-color: #81c784;
}

.section-title {
    font-weight: bold;
    margin-bottom: 10px;
    font-size: 18px;
    color: white;
}

.box p {
    margin: 0;
    padding: 0;
    line-height: 1.5;
}

.box:before {
    content: attr(data-index);
    position: absolute;
    top: 10px;
    right: 10px;
    font-size: 24px;
    font-weight: bold;
    color: rgba(255, 255, 255, 0.6);
}
